mapper.delete:
DELETE FROM T_BUILDER
WHERE
	F_CLASS_NAME = #{ className }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_CLASS_NAME AS "className", T1.F_SURROGATE_COUNT AS "surrogateCount", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", '' AS "_title" FROM T_BUILDER T1
LIMIT #{limit}
OFFSET #{offset}
