mapper.delete:
DELETE FROM T_CLASS
WHERE
	F_CLASS_ID = #{ classId }

mapper.select:
SELECT F_CLASS_ID AS "classId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_CLASS_NAME AS "className", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_CLASS
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_CLASS_ID AS "classId",
	F_OWNER_PROJECT_ID AS "ownerProjectId",
	F_CLASS_NAME AS "className",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_CLASS


		AND F_CLASS_NAME = #{ className }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}

SELECT F_FIELD_ID AS "fieldId", F_OWNER_CLASS_ID AS "ownerClassId", F_FIELD_NAME AS "fieldName", F_TYPE AS "type", F_WIDTH AS "width", F_SCALE AS "scale", F_CREF_CLASS_ID AS "crefClassId", F_EREF_ENUM_ID AS "erefEnumId", F_FREF_FIELD_ID AS "frefFieldId", F_PK AS "pk", F_NULLABLE AS "nullable", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_FIELD WHERE F_OWNER_CLASS_ID = #{ dto.classId }
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_FIELD_ID AS "fieldId",
	F_OWNER_CLASS_ID AS "ownerClassId",
	F_FIELD_NAME AS "fieldName",
	F_TYPE AS "type",
	F_WIDTH AS "width",
	F_SCALE AS "scale",
	F_CREF_CLASS_ID AS "crefClassId",
	F_EREF_ENUM_ID AS "erefEnumId",
	F_FREF_FIELD_ID AS "frefFieldId",
	F_PK AS "pk",
	F_NULLABLE AS "nullable",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_FIELD
WHERE
	F_OWNER_CLASS_ID = #{ dto.classId }

		AND F_FIELD_NAME = #{ fieldName }


		AND F_TYPE = #{ type }


		AND F_WIDTH = #{ width }


		AND F_SCALE = #{ scale }


		AND F_PK = #{ pk }


		AND F_NULLABLE = #{ nullable }


		AND F_TITLE = #{ title }


	LIMIT #{limit}

	OFFSET #{offset}
