mapper.update:
UPDATE T_CLASS SET
	F_OWNER_PROJECT_ID = #{ownerProjectId},
	F_CLASS_NAME = #{className}
	F_TITLE = #{title},
	F_DESCRIPTION = #{description},
	F_NOTE = #{note}
WHERE
	F_CLASS_ID = #{classId}

mapper.select:
SELECT F_FIELD_ID AS "fieldId", F_OWNER_CLASS_ID AS "ownerClassId", F_FIELD_NAME AS "fieldName", F_TYPE AS "type", F_WIDTH AS "width", F_SCALE AS "scale", F_CREF_CLASS_ID AS "crefClassId", F_EREF_ENUM_ID AS "erefEnumId", F_FREF_FIELD_ID AS "frefFieldId", F_PK AS "pk", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_FIELD WHERE F_OWNER_CLASS_ID = #{c.classId}
LIMIT #{limit}
OFFSET #{offset}
