mapper.delete:
DELETE FROM T_ENUM_I18N
WHERE
	F_OWNER_ENUM_ID = #{ ownerEnumId } AND 
	F_TARGET_LOCALE_ID = #{ targetLocaleId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_ENUM_ID AS "ownerEnumId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_ENUM_NAME || '(' || T2.F_TITLE || ')' AS "_title", T2.F_ENUM_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_TITLE AS "target_title" FROM T_ENUM_I18N T1 LEFT JOIN T_ENUM T2 ON T1.F_OWNER_ENUM_ID = T2.F_ENUM_ID LEFT JOIN T_LOCALE T3 ON T1.F_TARGET_LOCALE_ID = T3.F_LOCALE_ID ORDER BY T2.F_ENUM_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_ENUM T1
T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LOCALE_ID AS "localeId", T1.F_TITLE AS "_title" FROM T_LOCALE T1
T1.F_TITLE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
