mapper.delete:
DELETE FROM T_ENUM
WHERE
	F_ENUM_ID = #{ enumId }

mapper.select:
SELECT F_ENUM_ID AS "enumId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_ENUM_NAME AS "enumName", F_ENCODE_STRING AS "encodeString", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_ENUM
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_ENUM_ID AS "enumId",
	F_OWNER_PROJECT_ID AS "ownerProjectId",
	F_ENUM_NAME AS "enumName",
	F_ENCODE_STRING AS "encodeString",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_ENUM


		AND F_OWNER_PROJECT_ID = #{ ownerProjectId }


		AND F_ENUM_NAME = #{ enumName }


		AND F_ENCODE_STRING = #{ encodeString }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}

SELECT F_VALUE_ID AS "valueId", F_OWNER_ENUM_ID AS "ownerEnumId", F_CODE AS "code", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_ENUM_VALUE WHERE F_OWNER_ENUM_ID = #{ dto.enumId }
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_VALUE_ID AS "valueId",
	F_OWNER_ENUM_ID AS "ownerEnumId",
	F_CODE AS "code",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_ENUM_VALUE
WHERE
	F_OWNER_ENUM_ID = #{ dto.enumId }

		AND F_CODE = #{ code }


		AND F_TITLE = #{ title }


	LIMIT #{limit}

	OFFSET #{offset}
