mapper.delete:
DELETE FROM T_ENUM
WHERE
	F_ENUM_ID = #{ enumId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT F_ENUM_ID AS "enumId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_ENUM_NAME AS "enumName", F_ENCODE_STRING AS "encodeString", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_ENUM ORDER BY F_ENUM_ID
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_ENUM_NAME AS "enumName", T1.F_ENCODE_STRING AS "encodeString", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_ENUM T1 WHERE
T1.F_ENUM_NAME = #{name}
ORDER BY T1.F_ENUM_ID
LIMIT #{limit}
OFFSET #{offset}

SELECT F_OWNER_ENUM_ID AS "ownerEnumId", F_VALUE_ID AS "valueId", F_CODE AS "code", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_ENUM_VALUE WHERE F_OWNER_ENUM_ID = #{ content.enumId } ORDER BY F_CODE
LIMIT #{limit}
OFFSET #{offset}
