mapper.select:
SELECT F_VALUE_ID AS "valueId", F_OWNER_ENUM_ID AS "ownerEnumId", F_CODE AS "code", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_ENUM_VALUE
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_VALUE_ID AS "valueId",
	F_OWNER_ENUM_ID AS "ownerEnumId",
	F_CODE AS "code",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_ENUM_VALUE


		AND F_CODE = #{ code }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}
