mapper.delete:
DELETE FROM T_ENUM_VALUE
WHERE
	F_OWNER_ENUM_ID = #{ ownerEnumId } AND 
	F_VALUE_ID = #{ valueId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_ENUM_ID AS "ownerEnumId", T1.F_VALUE_ID AS "valueId", T1.F_CODE AS "code", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_VALUE_ID AS "_title", T2.F_ENUM_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_ENUM_VALUE T1 LEFT JOIN T_ENUM T2 ON T1.F_OWNER_ENUM_ID = T2.F_ENUM_ID ORDER BY T1.F_CODE ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_ENUM T1
T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
