mapper.delete:
DELETE FROM T_FIELD_I18N
WHERE
	F_OWNER_FIELD_ID = #{ ownerFieldId } AND 
	F_TARGET_LOCALE_ID = #{ targetLocaleId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_FIELD_ID AS "ownerFieldId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_FORMAT AS "format", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T3.F_CLASS_NAME || '/' || T2.F_FIELD_NAME AS "_title", T3.F_CLASS_NAME || '/' || T2.F_FIELD_NAME AS "owner_title", T4.F_TITLE AS "target_title" FROM T_FIELD_I18N T1 LEFT JOIN T_FIELD T2 ON T1.F_OWNER_FIELD_ID = T2.F_FIELD_ID LEFT JOIN T_CLASS T3 ON T2.F_OWNER_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_LOCALE T4 ON T1.F_TARGET_LOCALE_ID = T4.F_LOCALE_ID ORDER BY T2.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FIELD_ID AS "fieldId", T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME AS "_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID
T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LOCALE_ID AS "localeId", T1.F_TITLE AS "_title" FROM T_LOCALE T1
T1.F_TITLE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
