mapper.delete:
DELETE FROM T_FIELD
WHERE
	F_FIELD_ID = #{ fieldId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_FIELD_ID AS "fieldId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_FIELD_NAME AS "fieldName", T1.F_TYPE AS "type", T1.F_WIDTH AS "width", T1.F_SCALE AS "scale", T1.F_CREF_CLASS_ID AS "crefClassId", T1.F_EREF_ENUM_ID AS "erefEnumId", T1.F_FREF_FIELD_ID AS "frefFieldId", T1.F_PK AS "pk", T1.F_NULLABLE AS "nullable", T1.F_NEEDS_SUM AS "needsSum", T1.F_NEEDS_AVG AS "needsAvg", T1.F_NEEDS_MAX AS "needsMax", T1.F_NEEDS_MIN AS "needsMin", T1.F_IS_CONTAINER AS "isContainer", T1.F_FORMAT AS "format", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME AS "_title", T2.F_CLASS_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_CLASS_NAME || '(' || T3.F_TITLE || ')' AS "cref_title", T4.F_ENUM_NAME || '(' || T4.F_TITLE || ')' AS "eref_title", T6.F_CLASS_NAME || '/' || T5.F_FIELD_NAME AS "fref_title", T7.F_VALUE_ID AS "type_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID LEFT JOIN T_CLASS T3 ON T1.F_CREF_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_ENUM T4 ON T1.F_EREF_ENUM_ID = T4.F_ENUM_ID LEFT JOIN T_FIELD T5 ON T1.F_FREF_FIELD_ID = T5.F_FIELD_ID LEFT JOIN T_CLASS T6 ON T5.F_OWNER_CLASS_ID = T6.F_CLASS_ID LEFT JOIN T_ENUM_VALUE T7 ON T7.F_OWNER_ENUM_ID = 1 AND T1.F_TYPE = T7.F_VALUE_ID ORDER BY T1.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FIELD_ID AS "fieldId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_FIELD_NAME AS "fieldName", T1.F_TYPE AS "type", T1.F_WIDTH AS "width", T1.F_SCALE AS "scale", T1.F_CREF_CLASS_ID AS "crefClassId", T1.F_EREF_ENUM_ID AS "erefEnumId", T1.F_FREF_FIELD_ID AS "frefFieldId", T1.F_PK AS "pk", T1.F_NULLABLE AS "nullable", T1.F_NEEDS_SUM AS "needsSum", T1.F_NEEDS_AVG AS "needsAvg", T1.F_NEEDS_MAX AS "needsMax", T1.F_NEEDS_MIN AS "needsMin", T1.F_IS_CONTAINER AS "isContainer", T1.F_FORMAT AS "format", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME AS "_title", T2.F_CLASS_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_CLASS_NAME || '(' || T3.F_TITLE || ')' AS "cref_title", T4.F_ENUM_NAME || '(' || T4.F_TITLE || ')' AS "eref_title", T6.F_CLASS_NAME || '/' || T5.F_FIELD_NAME AS "fref_title", T7.F_VALUE_ID AS "type_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID LEFT JOIN T_CLASS T3 ON T1.F_CREF_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_ENUM T4 ON T1.F_EREF_ENUM_ID = T4.F_ENUM_ID LEFT JOIN T_FIELD T5 ON T1.F_FREF_FIELD_ID = T5.F_FIELD_ID LEFT JOIN T_CLASS T6 ON T5.F_OWNER_CLASS_ID = T6.F_CLASS_ID LEFT JOIN T_ENUM_VALUE T7 ON T7.F_OWNER_ENUM_ID = 1 AND T1.F_TYPE = T7.F_VALUE_ID WHERE
T1.F_FIELD_NAME = #{name}
ORDER BY T1.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_CLASS T1
T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_CLASS T1
T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_ENUM T1
T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FIELD_ID AS "fieldId", T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME AS "_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID
T2.F_CLASS_NAME || '/' || T1.F_FIELD_NAME LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_OWNER_FIELD_ID AS "ownerFieldId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_FORMAT AS "format", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T3.F_CLASS_NAME || '/' || T2.F_FIELD_NAME AS "_title", T3.F_CLASS_NAME || '/' || T2.F_FIELD_NAME AS "owner_title", T4.F_TITLE AS "target_title" FROM T_FIELD_I18N T1 LEFT JOIN T_FIELD T2 ON T1.F_OWNER_FIELD_ID = T2.F_FIELD_ID LEFT JOIN T_CLASS T3 ON T2.F_OWNER_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_LOCALE T4 ON T1.F_TARGET_LOCALE_ID = T4.F_LOCALE_ID WHERE T1.F_OWNER_FIELD_ID = #{ content.fieldId } ORDER BY T2.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}
