mapper.delete:
DELETE FROM T_FIELD
WHERE
	F_FIELD_ID = #{ fieldId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_FIELD_ID AS "fieldId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_FIELD_NAME AS "fieldName", T1.F_TYPE AS "type", T1.F_WIDTH AS "width", T1.F_SCALE AS "scale", T1.F_CREF_CLASS_ID AS "crefClassId", T1.F_EREF_ENUM_ID AS "erefEnumId", T1.F_FREF_FIELD_ID AS "frefFieldId", T1.F_PK AS "pk", T1.F_NULLABLE AS "nullable", T1.F_NEEDS_SUM AS "needsSum", T1.F_NEEDS_AVG AS "needsAvg", T1.F_NEEDS_MAX AS "needsMax", T1.F_NEEDS_MIN AS "needsMin", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_TITLE AS "_title", T2.F_TITLE AS "owner_title", T3.F_TITLE AS "cref_title", T4.F_TITLE AS "eref_title", T5.F_TITLE AS "fref_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID LEFT JOIN T_CLASS T3 ON T1.F_CREF_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_ENUM T4 ON T1.F_EREF_ENUM_ID = T4.F_ENUM_ID LEFT JOIN T_FIELD T5 ON T1.F_FREF_FIELD_ID = T5.F_FIELD_ID ORDER BY T1.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FIELD_ID AS "fieldId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_FIELD_NAME AS "fieldName", T1.F_TYPE AS "type", T1.F_WIDTH AS "width", T1.F_SCALE AS "scale", T1.F_CREF_CLASS_ID AS "crefClassId", T1.F_EREF_ENUM_ID AS "erefEnumId", T1.F_FREF_FIELD_ID AS "frefFieldId", T1.F_PK AS "pk", T1.F_NULLABLE AS "nullable", T1.F_NEEDS_SUM AS "needsSum", T1.F_NEEDS_AVG AS "needsAvg", T1.F_NEEDS_MAX AS "needsMax", T1.F_NEEDS_MIN AS "needsMin", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_TITLE AS "_title", T2.F_TITLE AS "owner_title", T3.F_TITLE AS "cref_title", T4.F_TITLE AS "eref_title", T5.F_TITLE AS "fref_title" FROM T_FIELD T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID LEFT JOIN T_CLASS T3 ON T1.F_CREF_CLASS_ID = T3.F_CLASS_ID LEFT JOIN T_ENUM T4 ON T1.F_EREF_ENUM_ID = T4.F_ENUM_ID LEFT JOIN T_FIELD T5 ON T1.F_FREF_FIELD_ID = T5.F_FIELD_ID WHERE
T1.F_FIELD_NAME = #{name}
ORDER BY T1.F_FIELD_ID ASC
LIMIT #{limit}
OFFSET #{offset}
