mapper.delete:
DELETE FROM T_FIELD
WHERE
	F_FIELD_ID = #{ fieldId }

mapper.select:
SELECT F_FIELD_ID AS "fieldId", F_OWNER_CLASS_ID AS "ownerClassId", F_FIELD_NAME AS "fieldName", F_TYPE AS "type", F_WIDTH AS "width", F_SCALE AS "scale", F_CREF_CLASS_ID AS "crefClassId", F_EREF_ENUM_ID AS "erefEnumId", F_FREF_FIELD_ID AS "frefFieldId", F_PK AS "pk", F_NULLABLE AS "nullable", F_NEEDS_SUM AS "needsSum", F_NEEDS_AVG AS "needsAvg", F_NEEDS_MAX AS "needsMax", F_NEEDS_MIN AS "needsMin", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_FIELD
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FIELD_ID AS "fieldId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_FIELD_NAME AS "fieldName", T1.F_TYPE AS "type", T1.F_WIDTH AS "width", T1.F_SCALE AS "scale", T1.F_CREF_CLASS_ID AS "crefClassId", T1.F_EREF_ENUM_ID AS "erefEnumId", T1.F_FREF_FIELD_ID AS "frefFieldId", T1.F_PK AS "pk", T1.F_NULLABLE AS "nullable", T1.F_NEEDS_SUM AS "needsSum", T1.F_NEEDS_AVG AS "needsAvg", T1.F_NEEDS_MAX AS "needsMax", T1.F_NEEDS_MIN AS "needsMin", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note" FROM T_FIELD T1 WHERE
(T1.F_PK == 1)
LIMIT #{limit}
OFFSET #{offset}

SELECT
	COUNT(*) AS "count"
FROM
	T_FIELD


		AND F_OWNER_CLASS_ID = #{ ownerClassId }


		AND F_FIELD_NAME = #{ fieldName }


		AND F_TYPE = #{ type }


		AND F_WIDTH = #{ width }


		AND F_SCALE = #{ scale }


		AND F_CREF_CLASS_ID = #{ crefClassId }


		AND F_EREF_ENUM_ID = #{ erefEnumId }


		AND F_FREF_FIELD_ID = #{ frefFieldId }


		AND F_PK = #{ pk }


		AND F_NULLABLE = #{ nullable }


		AND F_NEEDS_SUM = #{ needsSum }


		AND F_NEEDS_AVG = #{ needsAvg }


		AND F_NEEDS_MAX = #{ needsMax }


		AND F_NEEDS_MIN = #{ needsMin }


		AND F_TITLE = #{ title }

SELECT
	F_FIELD_ID AS "fieldId",
	F_OWNER_CLASS_ID AS "ownerClassId",
	F_FIELD_NAME AS "fieldName",
	F_TYPE AS "type",
	F_WIDTH AS "width",
	F_SCALE AS "scale",
	F_CREF_CLASS_ID AS "crefClassId",
	F_EREF_ENUM_ID AS "erefEnumId",
	F_FREF_FIELD_ID AS "frefFieldId",
	F_PK AS "pk",
	F_NULLABLE AS "nullable",
	F_NEEDS_SUM AS "needsSum",
	F_NEEDS_AVG AS "needsAvg",
	F_NEEDS_MAX AS "needsMax",
	F_NEEDS_MIN AS "needsMin",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_FIELD


		AND F_OWNER_CLASS_ID = #{ ownerClassId }


		AND F_FIELD_NAME = #{ fieldName }


		AND F_TYPE = #{ type }


		AND F_WIDTH = #{ width }


		AND F_SCALE = #{ scale }


		AND F_CREF_CLASS_ID = #{ crefClassId }


		AND F_EREF_ENUM_ID = #{ erefEnumId }


		AND F_FREF_FIELD_ID = #{ frefFieldId }


		AND F_PK = #{ pk }


		AND F_NULLABLE = #{ nullable }


		AND F_NEEDS_SUM = #{ needsSum }


		AND F_NEEDS_AVG = #{ needsAvg }


		AND F_NEEDS_MAX = #{ needsMax }


		AND F_NEEDS_MIN = #{ needsMin }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}
