mapper.delete:
DELETE FROM T_FIELD
WHERE
	F_FIELD_ID = #{ fieldId }

mapper.select:
SELECT F_FIELD_ID AS "fieldId", F_OWNER_CLASS_ID AS "ownerClassId", F_FIELD_NAME AS "fieldName", F_TYPE AS "type", F_WIDTH AS "width", F_SCALE AS "scale", F_CREF_CLASS_ID AS "crefClassId", F_EREF_ENUM_ID AS "erefEnumId", F_FREF_FIELD_ID AS "frefFieldId", F_PK AS "pk", F_NULLABLE AS "nullable", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_FIELD
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_FIELD_ID AS "fieldId",
	F_OWNER_CLASS_ID AS "ownerClassId",
	F_FIELD_NAME AS "fieldName",
	F_TYPE AS "type",
	F_WIDTH AS "width",
	F_SCALE AS "scale",
	F_CREF_CLASS_ID AS "crefClassId",
	F_EREF_ENUM_ID AS "erefEnumId",
	F_FREF_FIELD_ID AS "frefFieldId",
	F_PK AS "pk",
	F_NULLABLE AS "nullable",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_FIELD


		AND F_OWNER_CLASS_ID = #{ ownerClassId }


		AND F_FIELD_NAME = #{ fieldName }


		AND F_TYPE = #{ type }


		AND F_WIDTH = #{ width }


		AND F_SCALE = #{ scale }


		AND F_CREF_CLASS_ID = #{ crefClassId }


		AND F_EREF_ENUM_ID = #{ erefEnumId }


		AND F_FREF_FIELD_ID = #{ frefFieldId }


		AND F_PK = #{ pk }


		AND F_NULLABLE = #{ nullable }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}
