mapper.delete:
DELETE FROM T_FORM
WHERE
	F_FORM_ID = #{ formId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_FORM_ID AS "formId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_NAME AS "name", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_FORM T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID ORDER BY T1.F_NAME
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_VARIANT_ID AS "variantId", T1.F_OWNER_FORM_ID AS "ownerFormId", T1.F_WIDTH AS "width", T1.F_HEIGHT AS "height", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_NAME AS "_title", T2.F_NAME AS "owner_title" FROM T_FORM_VARIANT T1 LEFT JOIN T_FORM T2 ON T1.F_OWNER_FORM_ID = T2.F_FORM_ID WHERE T1.F_OWNER_FORM_ID = #{ content.formId } ORDER BY T2.F_NAME ASC, T1.F_WIDTH ASC
LIMIT #{limit}
OFFSET #{offset}
