mapper.delete:
DELETE FROM T_FORM_VARIANT
WHERE
	F_VARIANT_ID = #{ variantId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_VARIANT_ID AS "variantId", T1.F_OWNER_FORM_ID AS "ownerFormId", T1.F_WIDTH AS "width", T1.F_HEIGHT AS "height", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_NAME AS "_title", T2.F_NAME AS "owner_title" FROM T_FORM_VARIANT T1 LEFT JOIN T_FORM T2 ON T1.F_OWNER_FORM_ID = T2.F_FORM_ID ORDER BY T2.F_NAME ASC, T1.F_WIDTH ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_FORM_ID AS "formId", T1.F_NAME AS "_title" FROM T_FORM T1
T1.F_NAME LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LAYOUT_ID AS "layoutId", T1.F_OWNER_VARIANT_ID AS "ownerVariantId", T1.F_NAME AS "name", T1.F_PARENT_LAYOUT_ID AS "parentLayoutId", T1.F_TARGET_TYPE AS "targetType", T1.F_TARGET_FIELD_FIELD_ID AS "targetFieldFieldId", T1.F_TARGET_MESSAGE_MESSAGE_ID AS "targetMessageMessageId", T1.F_STYLE_STYLE_ID AS "styleStyleId", T1.F_LEFT AS "left", T1.F_TOP AS "top", T1.F_RIGHT AS "right", T1.F_BOTTOM AS "bottom", T1.F_WIDTH AS "width", T1.F_HEIGHT AS "height", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_NAME AS "_title", T3.F_NAME AS "owner_title", T4.F_NAME AS "parent_title", T6.F_CLASS_NAME || '/' || T5.F_FIELD_NAME AS "targetField_title", T7.F_MESSAGE_NAME AS "targetMessage_title", T8.F_NAME AS "style_title", T9.F_VALUE_ID AS "targetType_title" FROM T_LAYOUT T1 LEFT JOIN T_FORM_VARIANT T2 ON T1.F_OWNER_VARIANT_ID = T2.F_VARIANT_ID LEFT JOIN T_FORM T3 ON T2.F_OWNER_FORM_ID = T3.F_FORM_ID LEFT JOIN T_LAYOUT T4 ON T1.F_PARENT_LAYOUT_ID = T4.F_LAYOUT_ID LEFT JOIN T_FIELD T5 ON T1.F_TARGET_FIELD_FIELD_ID = T5.F_FIELD_ID LEFT JOIN T_CLASS T6 ON T5.F_OWNER_CLASS_ID = T6.F_CLASS_ID LEFT JOIN T_MESSAGE T7 ON T1.F_TARGET_MESSAGE_MESSAGE_ID = T7.F_MESSAGE_ID LEFT JOIN T_STYLE T8 ON T1.F_STYLE_STYLE_ID = T8.F_STYLE_ID LEFT JOIN T_ENUM_VALUE T9 ON T9.F_OWNER_ENUM_ID = 3 AND T1.F_TARGET_TYPE = T9.F_VALUE_ID WHERE T1.F_OWNER_VARIANT_ID = #{ content.variantId } ORDER BY T1.F_NAME
LIMIT #{limit}
OFFSET #{offset}
