mapper.delete:
DELETE FROM T_LOCALE
WHERE
	F_LOCALE_ID = #{ localeId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_LOCALE_ID AS "localeId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_TITLE AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_LOCALE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID ORDER BY T1.F_TITLE
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
