mapper.delete:
DELETE FROM T_LOCALIZED_RESOURCE
WHERE
	F_OWNER_RESOURCE_ID = #{ ownerResourceId } AND 
	F_LOCALE = #{ locale }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_RESOURCE_ID AS "ownerResourceId", T1.F_LOCALE AS "locale", T1.F_VALUE AS "value", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_VALUE AS "_title", T2.F_DEFAULT_VALUE AS "owner_title" FROM T_LOCALIZED_RESOURCE T1 LEFT JOIN T_RESOURCE T2 ON T1.F_OWNER_RESOURCE_ID = T2.F_RESOURCE_ID ORDER BY T2.F_RESOURCE_ID ASC, T1.F_LOCALE ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_RESOURCE_ID AS "resourceId", T1.F_DEFAULT_VALUE AS "_title" FROM T_RESOURCE T1
T1.F_DEFAULT_VALUE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
