mapper.delete:
DELETE FROM T_MESSAGE_I18N
WHERE
	F_OWNER_MESSAGE_ID = #{ ownerMessageId } AND 
	F_TARGET_LOCALE_ID = #{ targetLocaleId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_MESSAGE_ID AS "ownerMessageId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_MESSAGE AS "message", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_MESSAGE_NAME AS "_title", T2.F_MESSAGE_NAME AS "owner_title", T3.F_TITLE AS "target_title" FROM T_MESSAGE_I18N T1 LEFT JOIN T_MESSAGE T2 ON T1.F_OWNER_MESSAGE_ID = T2.F_MESSAGE_ID LEFT JOIN T_LOCALE T3 ON T1.F_TARGET_LOCALE_ID = T3.F_LOCALE_ID ORDER BY T2.F_MESSAGE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_MESSAGE_ID AS "messageId", T1.F_MESSAGE_NAME AS "_title" FROM T_MESSAGE T1
T1.F_MESSAGE_NAME LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LOCALE_ID AS "localeId", T1.F_TITLE AS "_title" FROM T_LOCALE T1
T1.F_TITLE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
