mapper.delete:
DELETE FROM T_MESSAGE
WHERE
	F_MESSAGE_ID = #{ messageId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_MESSAGE_ID AS "messageId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_MESSAGE_NAME AS "messageName", T1.F_MESSAGE_RESOURCE_ID AS "messageResourceId", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_MESSAGE_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_DEFAULT_VALUE AS "message_title" FROM T_MESSAGE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID LEFT JOIN T_RESOURCE T3 ON T1.F_MESSAGE_RESOURCE_ID = T3.F_RESOURCE_ID ORDER BY T1.F_MESSAGE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_RESOURCE_ID AS "resourceId", T1.F_DEFAULT_VALUE AS "_title" FROM T_RESOURCE T1
T1.F_DEFAULT_VALUE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
