mapper.select:
SELECT T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_MESSAGE_ID AS "messageId", T1.F_LOCALE_ID AS "localeId", T1.F_TEXT AS "text", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_MESSAGE_ID || '(' || T1.F_LOCALE_ID || ')' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_MESSAGE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID ORDER BY T1.F_LOCALE_ID ASC, T1.F_MESSAGE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
