mapper.delete:
DELETE FROM T_PAGE_SET
WHERE
	F_PAGE_SET_ID = #{ pageSetId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_PAGE_SET_ID AS "pageSetId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_PAGE_SET_NAME AS "pageSetName", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_PAGE_SET_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_PAGE_SET T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID ORDER BY T1.F_PAGE_SET_NAME
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PAGE_ID AS "pageId", T1.F_OWNER_PAGE_SET_ID AS "ownerPageSetId", T1.F_CONTEXT_CLASS_ID AS "contextClassId", T1.F_WIDTH AS "width", T1.F_HEIGHT AS "height", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_PAGE_SET_NAME || '(' || T1.F_WIDTH || 'x' || T1.F_HEIGHT || ')' AS "_title", T2.F_PAGE_SET_NAME AS "owner_title", T3.F_CLASS_NAME || '(' || T3.F_TITLE || ')' AS "context_title" FROM T_PAGE T1 LEFT JOIN T_PAGE_SET T2 ON T1.F_OWNER_PAGE_SET_ID = T2.F_PAGE_SET_ID LEFT JOIN T_CLASS T3 ON T1.F_CONTEXT_CLASS_ID = T3.F_CLASS_ID WHERE T1.F_OWNER_PAGE_SET_ID = #{ content.pageSetId } ORDER BY T1.F_WIDTH DESC, T1.F_HEIGHT DESC
LIMIT #{limit}
OFFSET #{offset}
