mapper.delete:
DELETE FROM T_PROJECT_I18N
WHERE
	F_OWNER_PROJECT_ID = #{ ownerProjectId } AND 
	F_TARGET_LOCALE_ID = #{ targetLocaleId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || '/I18n)' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_TITLE AS "target_title" FROM T_PROJECT_I18N T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID LEFT JOIN T_LOCALE T3 ON T1.F_TARGET_LOCALE_ID = T3.F_LOCALE_ID ORDER BY T2.F_PROJECT_ID ASC, T3.F_LOCALE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LOCALE_ID AS "localeId", T1.F_TITLE AS "_title" FROM T_LOCALE T1
T1.F_TITLE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
