mapper.update:
UPDATE T_PROJECT SET
	F_PROJECT_NAME = #{projectName}
	F_TITLE = #{title},
	F_DESCRIPTION = #{description},
	F_NOTE = #{note},
	F_COPYRIGHTS = #{copyrights}
WHERE
	F_PROJECT_ID = #{projectId}

mapper.select:
SELECT F_PROJECT_ID AS "projectId", F_PROJECT_NAME AS "projectName", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F_COPYRIGHTS AS "copyrights" FROM T_PROJECT
LIMIT #{limit}
OFFSET #{offset}

SELECT F_CLASS_ID AS "classId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_CLASS_NAME AS "className", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_CLASS WHERE F_OWNER_PROJECT_ID = #{project.projectId}
LIMIT #{limit}
OFFSET #{offset}

SELECT F_ENUM_ID AS "enumId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_ENUM_NAME AS "enumName", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_ENUM WHERE F_OWNER_PROJECT_ID = #{project.projectId}
LIMIT #{limit}
OFFSET #{offset}
