mapper.delete:
DELETE FROM T_PROJECT
WHERE
	F_PROJECT_ID = #{ projectId }

mapper.select:
SELECT F_PROJECT_ID AS "projectId", F_PROJECT_NAME AS "projectName", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F_COPYRIGHTS AS "copyrights" FROM T_PROJECT
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_PROJECT_ID AS "projectId",
	F_PROJECT_NAME AS "projectName",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note",
	F_COPYRIGHTS AS "copyrights"
FROM
	T_PROJECT


		AND F_PROJECT_NAME = #{ projectName }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}

SELECT F_CLASS_ID AS "classId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_CLASS_NAME AS "className", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_CLASS WHERE F_OWNER_PROJECT_ID = #{ dto.projectId }
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_CLASS_ID AS "classId",
	F_OWNER_PROJECT_ID AS "ownerProjectId",
	F_CLASS_NAME AS "className",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_CLASS
WHERE
	F_OWNER_PROJECT_ID = #{ dto.projectId }

		AND F_OWNER_PROJECT_ID = #{ ownerProjectId }


		AND F_CLASS_NAME = #{ className }


		AND F_TITLE = #{ title }


	LIMIT #{limit}

	OFFSET #{offset}

SELECT F_ENUM_ID AS "enumId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_ENUM_NAME AS "enumName", F_ENCODE_STRING AS "encodeString", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_ENUM WHERE F_OWNER_PROJECT_ID = #{ dto.projectId }
LIMIT #{limit}
OFFSET #{offset}

SELECT
	F_ENUM_ID AS "enumId",
	F_OWNER_PROJECT_ID AS "ownerProjectId",
	F_ENUM_NAME AS "enumName",
	F_ENCODE_STRING AS "encodeString",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_ENUM
WHERE
	F_OWNER_PROJECT_ID = #{ dto.projectId }

		AND F_OWNER_PROJECT_ID = #{ ownerProjectId }


		AND F_ENUM_NAME = #{ enumName }


		AND F_ENCODE_STRING = #{ encodeString }


		AND F_TITLE = #{ title }


	LIMIT #{limit}

	OFFSET #{offset}
