mapper.delete:
DELETE FROM T_PROJECT
WHERE
	F_PROJECT_ID = #{ projectId }

mapper.select:
SELECT F_PROJECT_ID AS "projectId", F_PROJECT_NAME AS "projectName", F_GRADLE_NAME AS "gradleName", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F_COPYRIGHTS AS "copyrights", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_PROJECT
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME AS "projectName", T1.F_GRADLE_NAME AS "gradleName", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F_COPYRIGHTS AS "copyrights", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_PROJECT T1 WHERE
T1.F_PROJECT_NAME = #{name}
LIMIT #{limit}
OFFSET #{offset}

SELECT F_CLASS_ID AS "classId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_CLASS_NAME AS "className", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_CLASS WHERE F_OWNER_PROJECT_ID = #{ content.projectId }
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_CLASS_NAME AS "className", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_CLASS T1 WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId }
AND T1.F_CLASS_NAME = #{name}
LIMIT #{limit}
OFFSET #{offset}

SELECT F_ENUM_ID AS "enumId", F_OWNER_PROJECT_ID AS "ownerProjectId", F_ENUM_NAME AS "enumName", F_ENCODE_STRING AS "encodeString", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_ENUM WHERE F_OWNER_PROJECT_ID = #{ content.projectId }
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_ENUM_NAME AS "enumName", T1.F_ENCODE_STRING AS "encodeString", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_ENUM T1 WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId }
AND T1.F_ENUM_NAME = #{name}
LIMIT #{limit}
OFFSET #{offset}
