mapper.delete:
DELETE FROM T_PROJECT
WHERE
	F_PROJECT_ID = #{ projectId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME AS "projectName", T1.F_GRADLE_NAME AS "gradleName", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F_COPYRIGHTS AS "copyrights", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1 ORDER BY T1.F_PROJECT_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME AS "projectName", T1.F_GRADLE_NAME AS "gradleName", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F_COPYRIGHTS AS "copyrights", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1 WHERE
T1.F_PROJECT_NAME = #{name}
ORDER BY T1.F_PROJECT_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_CLASS_NAME AS "className", T1.F_ORDER_EXPR AS "orderExpr", T1.F_TITLE_EXPR AS "titleExpr", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_CLASS T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId } ORDER BY T1.F_CLASS_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_CLASS_NAME AS "className", T1.F_ORDER_EXPR AS "orderExpr", T1.F_TITLE_EXPR AS "titleExpr", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_CLASS_NAME || '(' || T1.F_TITLE || ')' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_CLASS T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId }
AND T1.F_CLASS_NAME = #{name}
ORDER BY T1.F_CLASS_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_ENUM_NAME AS "enumName", T1.F_ENCODE_STRING AS "encodeString", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_ENUM T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId } ORDER BY T1.F_ENUM_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_ENUM_ID AS "enumId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_ENUM_NAME AS "enumName", T1.F_ENCODE_STRING AS "encodeString", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_ENUM_NAME || '(' || T1.F_TITLE || ')' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_ENUM T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId }
AND T1.F_ENUM_NAME = #{name}
ORDER BY T1.F_ENUM_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_MESSAGE_ID AS "messageId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_MESSAGE_NAME AS "messageName", T1.F_MESSAGE AS "message", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_MESSAGE_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_MESSAGE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId } ORDER BY T1.F_MESSAGE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_LOCALE_ID AS "localeId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_TITLE AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_LOCALE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId } ORDER BY T1.F_TITLE
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_TARGET_LOCALE_ID AS "targetLocaleId", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || '/I18n)' AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_TITLE AS "target_title" FROM T_PROJECT_I18N T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID LEFT JOIN T_LOCALE T3 ON T1.F_TARGET_LOCALE_ID = T3.F_LOCALE_ID WHERE T1.F_OWNER_PROJECT_ID = #{ content.projectId } ORDER BY T2.F_PROJECT_ID ASC, T3.F_LOCALE_ID ASC
LIMIT #{limit}
OFFSET #{offset}
