mapper.delete:
DELETE FROM T_QUERY
WHERE
	F_QUERY_ID = #{ queryId }

mapper.select:
SELECT F_QUERY_ID AS "queryId", F_OWNER_CLASS_ID AS "ownerClassId", F_QUERY_NAME AS "queryName", F_FILTER AS "filter", F_ORDER AS "order", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note" FROM T_QUERY
LIMIT #{limit}
OFFSET #{offset}

SELECT
	COUNT(*) AS "count"
FROM
	T_QUERY


		AND F_OWNER_CLASS_ID = #{ ownerClassId }


		AND F_QUERY_NAME = #{ queryName }


		AND F_FILTER = #{ filter }


		AND F_ORDER = #{ order }


		AND F_TITLE = #{ title }

SELECT
	F_QUERY_ID AS "queryId",
	F_OWNER_CLASS_ID AS "ownerClassId",
	F_QUERY_NAME AS "queryName",
	F_FILTER AS "filter",
	F_ORDER AS "order",
	F_TITLE AS "title",
	F_DESCRIPTION AS "description",
	F_NOTE AS "note"
FROM
	T_QUERY


		AND F_OWNER_CLASS_ID = #{ ownerClassId }


		AND F_QUERY_NAME = #{ queryName }


		AND F_FILTER = #{ filter }


		AND F_ORDER = #{ order }


		AND F_TITLE = #{ title }



	LIMIT #{limit}

	OFFSET #{offset}
