mapper.delete:
DELETE FROM T_QUERY
WHERE
	F_QUERY_ID = #{ queryId }

mapper.select:
SELECT F_QUERY_ID AS "queryId", F_OWNER_CLASS_ID AS "ownerClassId", F_QUERY_NAME AS "queryName", F_FILTER AS "filter", F_ORDER AS "order", F_TITLE AS "title", F_DESCRIPTION AS "description", F_NOTE AS "note", F__LAST_UPDATED_AT AS "_lastUpdatedAt" FROM T_QUERY
LIMIT #{limit}
OFFSET #{offset}
