mapper.delete:
DELETE FROM T_QUERY
WHERE
	F_QUERY_ID = #{ queryId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_QUERY_ID AS "queryId", T1.F_OWNER_CLASS_ID AS "ownerClassId", T1.F_QUERY_NAME AS "queryName", T1.F_FILTER AS "filter", T1.F_ORDER AS "order", T1.F_TITLE AS "title", T1.F_DESCRIPTION AS "description", T1.F_NOTE AS "note", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_TITLE AS "_title", T2.F_TITLE AS "owner_title" FROM T_QUERY T1 LEFT JOIN T_CLASS T2 ON T1.F_OWNER_CLASS_ID = T2.F_CLASS_ID ORDER BY T1.F_QUERY_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_CLASS_ID AS "classId", T1.F_TITLE AS "_title" FROM T_CLASS T1
T1.F_TITLE LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}
