mapper.delete:
DELETE FROM T_RESOURCE
WHERE
	F_RESOURCE_ID = #{ resourceId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_RESOURCE_ID AS "resourceId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_DEFAULT_VALUE AS "defaultValue", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_DEFAULT_VALUE AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title" FROM T_RESOURCE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID ORDER BY T1.F_RESOURCE_ID ASC
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_OWNER_RESOURCE_ID AS "ownerResourceId", T1.F_LOCALE AS "locale", T1.F_VALUE AS "value", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_VALUE AS "_title", T2.F_DEFAULT_VALUE AS "owner_title" FROM T_LOCALIZED_RESOURCE T1 LEFT JOIN T_RESOURCE T2 ON T1.F_OWNER_RESOURCE_ID = T2.F_RESOURCE_ID WHERE T1.F_OWNER_RESOURCE_ID = #{ content.resourceId } ORDER BY T2.F_RESOURCE_ID ASC, T1.F_LOCALE ASC
LIMIT #{limit}
OFFSET #{offset}
