mapper.delete:
DELETE FROM T_STYLE
WHERE
	F_STYLE_ID = #{ styleId }
	AND
	F__LAST_UPDATED_AT = #{ _lastUpdatedAt }

mapper.select:
SELECT T1.F_STYLE_ID AS "styleId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_STYLE_NAME AS "styleName", T1.F_PARENT_STYLE_ID AS "parentStyleId", T1.F_COND AS "cond", T1.F_COND_PARAM AS "condParam", T1.F_TEXT_COLOR AS "textColor", T1.F_BACKGROUND_COLOR AS "backgroundColor", T1.F_BACKGROUND_IMAGE AS "backgroundImage", T1.F_BORDER_LEFT AS "borderLeft", T1.F_BORDER_TOP AS "borderTop", T1.F_BORDER_RIGHT AS "borderRight", T1.F_BORDER_BOTTOM AS "borderBottom", T1.F_BORDER_COLOR AS "borderColor", T1.F_BORDER_IMAGE AS "borderImage", T1.F_FONT_SIZE AS "fontSize", T1.F_FONT_FAMILY AS "fontFamily", T1.F_LINE_HEIGHT AS "lineHeight", T1.F_TEXT_ALIGN AS "textAlign", T1.F_VERTICAL_ALIGN AS "verticalAlign", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_STYLE_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_STYLE_NAME AS "parent_title", T4.F_VALUE_ID AS "cond_title" FROM T_STYLE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID LEFT JOIN T_STYLE T3 ON T1.F_PARENT_STYLE_ID = T3.F_STYLE_ID LEFT JOIN T_ENUM_VALUE T4 ON T4.F_OWNER_ENUM_ID = 2 AND T1.F_COND = T4.F_VALUE_ID ORDER BY T1.F_STYLE_NAME
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_PROJECT_ID AS "projectId", T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' AS "_title" FROM T_PROJECT T1
T1.F_PROJECT_NAME || '(' || T1.F_TITLE || ')' LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_STYLE_ID AS "styleId", T1.F_STYLE_NAME AS "_title" FROM T_STYLE T1
T1.F_STYLE_NAME LIKE '%' || #{hint} || '%'
LIMIT #{limit}
OFFSET #{offset}

SELECT T1.F_STYLE_ID AS "styleId", T1.F_OWNER_PROJECT_ID AS "ownerProjectId", T1.F_STYLE_NAME AS "styleName", T1.F_PARENT_STYLE_ID AS "parentStyleId", T1.F_COND AS "cond", T1.F_COND_PARAM AS "condParam", T1.F_TEXT_COLOR AS "textColor", T1.F_BACKGROUND_COLOR AS "backgroundColor", T1.F_BACKGROUND_IMAGE AS "backgroundImage", T1.F_BORDER_LEFT AS "borderLeft", T1.F_BORDER_TOP AS "borderTop", T1.F_BORDER_RIGHT AS "borderRight", T1.F_BORDER_BOTTOM AS "borderBottom", T1.F_BORDER_COLOR AS "borderColor", T1.F_BORDER_IMAGE AS "borderImage", T1.F_FONT_SIZE AS "fontSize", T1.F_FONT_FAMILY AS "fontFamily", T1.F_LINE_HEIGHT AS "lineHeight", T1.F_TEXT_ALIGN AS "textAlign", T1.F_VERTICAL_ALIGN AS "verticalAlign", T1.F__LAST_UPDATED_AT AS "_lastUpdatedAt", T1.F_STYLE_NAME AS "_title", T2.F_PROJECT_NAME || '(' || T2.F_TITLE || ')' AS "owner_title", T3.F_STYLE_NAME AS "parent_title", T4.F_VALUE_ID AS "cond_title" FROM T_STYLE T1 LEFT JOIN T_PROJECT T2 ON T1.F_OWNER_PROJECT_ID = T2.F_PROJECT_ID LEFT JOIN T_STYLE T3 ON T1.F_PARENT_STYLE_ID = T3.F_STYLE_ID LEFT JOIN T_ENUM_VALUE T4 ON T4.F_OWNER_ENUM_ID = 2 AND T1.F_COND = T4.F_VALUE_ID WHERE T1.F_PARENT_STYLE_ID = #{ content.styleId } ORDER BY T1.F_STYLE_NAME
LIMIT #{limit}
OFFSET #{offset}
